    }

    return (
        <div>
            <input type={'text'} value={name} onChange={e => {
                setName(e.target.value)
            }
            }/>
            <button onClick={() => {
                test=test++;
            }}>Click {count} times</button>
        </div>
    );
};

export default UseEffect;
