import React, {useState, useEffect} from 'react';

function UseEffectInner(props) {
    // Pass useEffect a function
    //const [randomFromParent, setRandomFromParent] = useState(0)
    //const re = setRandomFromParent(props.theRandom)


    const [random, setRandom] = useState(1);
    const [mounted, setMounted] = useState(true);
    const reRender = () => setRandom(1);
    const toggle = () => setMounted(!mounted);
    useEffect(() => {
        console.log('render!');
        return () => console.log('unmounting...');
    },[random])
    //return "I'm a lifecycle demo";
    return (
        <>
            <button onClick={reRender}>Re-render</button>
            <button onClick={toggle}>Show/Hide LifecycleDemo</button>
            {/*{mounted && <UseEffectInner />}*/}
        </>
    );
}

function UseEffectOuter() {
    // Set up a piece of state, so that we have
    // a way to trigger a re-render.
    const [random, setRandom] = useState(Math.random());

    // Set up another piece of state to keep track of
    // whether the LifecycleDemo is shown or hidden
    const [mounted, setMounted] = useState(true);

    // This function will change the random number,
    // and trigger a re-render (in the console,
    // you'll see a "render!" from LifecycleDemo)
    const reRender = () => setRandom(Math.random());

    // This function will unmount and re-mount the
    // LifecycleDemo, so you can see its cleanup function
    // being called.
    const toggle = () => setMounted(!mounted);

    return (
        <>
            <button onClick={reRender}>Re-render</button>
            <button onClick={toggle}>Show/Hide LifecycleDemo</button>
            {mounted && <UseEffectInner />}
        </>
    );
}
export default UseEffectInner;
