import React, { Component } from 'react'

class EmployeeListLifecycle extends Component{
    constructor(props){
        super(props);

        this.state={
            names: props.initialNames,
        };

        console.log('1st - constructor ' + props.initialNames); 
    }

    deleteRandom = () => {
        var index = Math.floor(Math.random() * this.state.names.length);

        this.setState((prevState)=>{
            let names = prevState.names.concat();

            names.splice(index, 1);

            return{
                names: names
            }
        });
    }

    render (){
        let index = 0;
        return(
            <div>
                {this.state.names.map(
                    (name) => <EmployeeCyclePhasesm key={index++}
                                                    name={name}
                                                    deleteRandom={this.deleteRandom}/>
                )}
                <button onClick={this.deleteRandom}>Delete random employee</button>
            </div>
        )
    }
}

class EmployeeCyclePhases extends Component {
    constructor(props){
        super(props);
        console.log('1st - constructor '+this.props.name);
    }


    render() {
        console.log('2nd - render '+this.props.name);
        return (
            <div>
                <div>
                    {this.props.name}
                </div>
            </div>
        );
    }
    componentDidMount(){
        console.log('3rd - componentDidMount '+this.props.name);
        
    }

    componentWillUnmount(){
        console.log('4th - componentDidUnmount '+this.props.name);
        
    }
}

export default EmployeeCyclePhases;
